"use client";

import { Radio as RadioPrimitive } from "@base-ui/react/radio";
import { RadioGroup as RadioGroupPrimitive } from "@base-ui/react/radio-group";
import { CirclePlaceholderOnIcon } from "blode-icons-react";
import * as React from "react";

import { cn } from "@/lib/utils";

import "./radio-group.css";

type RadioGroupProps = Omit<
  React.ComponentPropsWithoutRef<typeof RadioGroupPrimitive>,
  "defaultValue" | "onValueChange" | "value"
> & {
  value?: string;
  defaultValue?: string;
  onValueChange?: (value: string) => void;
};

type RadioGroupItemProps = Omit<
  React.ComponentPropsWithoutRef<typeof RadioPrimitive.Root>,
  "value"
> & {
  value: string;
};

const RadioGroup = React.forwardRef<HTMLDivElement, RadioGroupProps>(
  ({ className, onValueChange, ...props }, ref) => {
    return (
      <RadioGroupPrimitive
        className={cn("grid w-full gap-2", className)}
        data-slot="radio-group"
        onValueChange={(nextValue) =>
          onValueChange?.(
            typeof nextValue === "string" ? nextValue : String(nextValue ?? "")
          )
        }
        ref={ref}
        {...props}
      />
    );
  }
);
RadioGroup.displayName = "RadioGroup";

const RadioGroupItem = React.forwardRef<HTMLSpanElement, RadioGroupItemProps>(
  ({ className, ...props }, ref) => {
    return (
      <RadioPrimitive.Root
        className={cn(
          "ft-radio peer relative inline-flex aspect-square size-5 shrink-0 cursor-pointer items-center justify-center overflow-hidden rounded-full bg-card align-middle text-primary-foreground shadow-input ring-offset-background",
          "focus:outline-hidden focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2",
          "data-disabled:cursor-not-allowed data-disabled:opacity-50",
          className
        )}
        ref={ref}
        render={(renderProps, state) => (
          <span
            {...renderProps}
            data-state={state.checked ? "checked" : "unchecked"}
          />
        )}
        {...props}
      >
        <RadioPrimitive.Indicator
          className="ft-radio-indicator pointer-events-none absolute inset-0 flex items-center justify-center text-current"
          keepMounted
        >
          <CirclePlaceholderOnIcon className="ft-radio-dot size-2.5 fill-current text-primary-foreground" />
        </RadioPrimitive.Indicator>
      </RadioPrimitive.Root>
    );
  }
);
RadioGroupItem.displayName = "RadioGroupItem";

export { RadioGroup, RadioGroupItem };

.ft-radio::after {
  position: absolute;
  inset: 0;
  content: "";
  background-color: var(--primary);
  border-radius: 9999px;
  opacity: 0;
  transform: scale(0.5);
  transform-origin: center;
  transition:
    transform 200ms linear,
    opacity 200ms linear;
}

.ft-radio[data-state="checked"]::after,
.ft-radio[data-checked]::after {
  opacity: 1;
  transform: scale(1);
}

.ft-radio::before {
  position: absolute;
  inset: 0;
  content: "";
  border: 1px solid var(--input);
  border-radius: 9999px;
  transition: border-color 200ms ease;
}

.ft-radio[data-state="checked"]::before,
.ft-radio[data-checked]::before {
  border-color: var(--primary);
}

.ft-radio:not([data-disabled]):hover::before {
  border-color: var(--input-hover, var(--input));
}

.ft-radio-indicator {
  z-index: 1;
  position: relative;
}

.ft-radio-indicator::before {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 0.625rem;
  height: 0.625rem;
  content: "";
  background-color: currentColor;
  border-radius: 9999px;
  opacity: 0;
  transform: translate(-50%, -50%) scale(0.3);
  transform-origin: center;
  transition:
    transform 200ms cubic-bezier(0.22, 1, 0.36, 1),
    opacity 180ms cubic-bezier(0.22, 1, 0.36, 1);
}

.ft-radio-dot {
  opacity: 0;
  transform: scale(0.25);
  transform-origin: center;
  transition:
    transform 220ms cubic-bezier(0.22, 1, 0.36, 1) 80ms,
    opacity 220ms cubic-bezier(0.22, 1, 0.36, 1) 80ms;
}

.ft-radio[data-state="checked"] .ft-radio-indicator::before,
.ft-radio[data-checked] .ft-radio-indicator::before {
  opacity: 0.2;
  transform: translate(-50%, -50%) scale(1.7);
}

.ft-radio[data-state="checked"] .ft-radio-dot,
.ft-radio[data-checked] .ft-radio-dot {
  opacity: 1;
  transform: scale(1);
}

.ft-radio[data-state="unchecked"] .ft-radio-indicator::before,
.ft-radio[data-unchecked] .ft-radio-indicator::before {
  opacity: 0;
  transform: translate(-50%, -50%) scale(0.3);
  transition:
    transform 140ms cubic-bezier(0.4, 0, 1, 1),
    opacity 120ms linear;
}

.ft-radio[data-state="unchecked"] .ft-radio-dot,
.ft-radio[data-unchecked] .ft-radio-dot {
  opacity: 0;
  transform: scale(0.25);
  transition:
    transform 140ms cubic-bezier(0.4, 0, 1, 1),
    opacity 120ms linear;
}

@media (prefers-reduced-motion: reduce) {
  .ft-radio::after,
  .ft-radio::before,
  .ft-radio-indicator::before,
  .ft-radio-dot {
    transition: none;
  }
}
